import React, { useRef } from 'react';
import './MainPage.css';
import showcaseImg1 from '../assets/placeholder1.jpg';
import showcaseImg2 from '../assets/placeholder2.jpg';
import showcaseImg3 from '../assets/console table.jpg';

const images = [showcaseImg1, showcaseImg2, showcaseImg3];

const MainPage = () => {
  const imageRefs = useRef(images.map(() => React.createRef()));
  const [currentIndex, setCurrentIndex] = React.useState(0);
  const [hoveredIndex, setHoveredIndex] = React.useState(null);

  // Scroll to the image at index
  const scrollToImage = (idx) => {
    setCurrentIndex(idx);
    imageRefs.current[idx].current.scrollIntoView({ behavior: 'smooth', block: 'center' });
  };

  // Handle scroll: update currentIndex based on which image is most in view
  React.useEffect(() => {
    const handleScroll = () => {
      const offsets = imageRefs.current.map(ref => {
        if (!ref.current) return Infinity;
        const rect = ref.current.getBoundingClientRect();
        return Math.abs(rect.top + rect.height / 2 - window.innerHeight / 2);
      });
      const minOffset = Math.min(...offsets);
      const idx = offsets.indexOf(minOffset);
      setCurrentIndex(idx);
    };
    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="main-showcase-vertical">
      <button
        className="fixed-arrow fixed-left-arrow"
        aria-label="Previous"
        onClick={() => scrollToImage(Math.max(0, currentIndex - 1))}
        disabled={currentIndex === 0}
      >
        &#x276E;
      </button>
      <button
        className="fixed-arrow fixed-right-arrow"
        aria-label="Next"
        onClick={() => scrollToImage(Math.min(images.length - 1, currentIndex + 1))}
        disabled={currentIndex === images.length - 1}
      >
        &#x276F;
      </button>
      {images.map((img, idx) => (
        <div
          className="main-showcase-container"
          key={idx}
          ref={imageRefs.current[idx]}
        >
          <div
            className="showcase-image-wrapper"
            onMouseEnter={() => setHoveredIndex(idx)}
            onMouseLeave={() => setHoveredIndex(null)}
          >
            <img
              src={img}
              alt={`Showcase ${idx + 1}`}
              className={`showcase-image ${hoveredIndex === idx ? 'zoom-in' : hoveredIndex === null ? '' : 'zoom-out'}`}
            />
          </div>
        </div>
      ))}
    </div>
  );
};

export default MainPage;
