import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AdminHeader from './AdminHeader';
import AdminProductManagement from './AdminProductManagement';
import { productAPI, authAPI, orderAPI } from '../services/api';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [activeMenuItem, setActiveMenuItem] = useState('Dashboard');
  const [dashboardData, setDashboardData] = useState({
    totalProducts: 0,
    totalCategories: 0,
    totalUsers: 0,
    websiteVisits: 0
  });
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Fetch dashboard data
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch products to get total count
      const productsResponse = await productAPI.getAllProducts();
      const totalProducts = productsResponse.success ? (productsResponse.data?.length || 0) : 0;
      
      // Get unique categories from products
      const categories = productsResponse.success && productsResponse.data 
        ? [...new Set(productsResponse.data.map(product => product.category).filter(Boolean))]
        : [];
      const totalCategories = categories.length;
      
      // Fetch users to get total count
      const adminToken = localStorage.getItem('adminToken');
      let totalUsers = 0;
      if (adminToken) {
        try {
          const usersResponse = await authAPI.getAllUsers(adminToken);
          totalUsers = usersResponse.success ? (usersResponse.data?.length || 0) : 0;
        } catch (error) {
          console.error('Error fetching users:', error);
          totalUsers = 0; // Fallback to 0 if API fails
        }
      }
      
      // For now, we'll use placeholder data for website visits
      // You can implement analytics API later
      const websiteVisits = 1234; // Placeholder - implement analytics API later
      
      setDashboardData({
        totalProducts,
        totalCategories,
        totalUsers,
        websiteVisits
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  // Function to refresh dashboard data (can be called from other components)
  const refreshDashboard = () => {
    fetchDashboardData();
  };

  // Function to handle KPI card clicks
  const handleKPIClick = (cardTitle) => {
    switch (cardTitle) {
      case 'TOTAL PRODUCTS':
        setActiveMenuItem('Products');
        break;
      case 'TOTAL USERS':
        setActiveMenuItem('Customers');
        break;
      default:
        break;
    }
  };

  const menuItems = [
    { name: 'Dashboard', icon: 'üìä' },
    { name: 'Products', icon: 'üì¶' },
    { name: 'Categories', icon: 'üìÇ' },
    { name: 'Locations', icon: 'üìç' },
    { name: 'Customers', icon: 'üë•' }
  ];

  const kpiCards = [
    {
      title: 'TOTAL PRODUCTS',
      value: loading ? '...' : dashboardData.totalProducts.toString(),
      icon: 'üì¶',
      color: '#3B82F6',
      bgColor: '#EFF6FF'
    },
    {
      title: 'TOTAL CATEGORIES',
      value: loading ? '...' : dashboardData.totalCategories.toString(),
      icon: 'üìÇ',
      color: '#8B5CF6',
      bgColor: '#F3F4F6'
    },
    {
      title: 'TOTAL USERS',
      value: loading ? '...' : dashboardData.totalUsers.toString(),
      icon: 'üë•',
      color: '#10B981',
      bgColor: '#ECFDF5'
    },
    {
      title: 'WEBSITE VISITS',
      value: loading ? '...' : dashboardData.websiteVisits.toLocaleString(),
      icon: 'üìä',
      color: '#F59E0B',
      bgColor: '#FFFBEB'
    }
  ];

  const [recentProducts, setRecentProducts] = useState([]);

  // Fetch recent products
  useEffect(() => {
    const fetchRecentProducts = async () => {
      try {
        const productsResponse = await productAPI.getAllProducts();
        if (productsResponse.success && productsResponse.data) {
          const recent = productsResponse.data
            .slice(0, 4) // Show only first 4 products
            .map(product => ({
              id: product.id,
              name: product.name,
              category: product.category,
              price: `$${product.price}`,
              stock: product.stock?.toString() || '0',
              status: product.stock > 5 ? 'In Stock' : 'Low Stock'
            }));
          setRecentProducts(recent);
        }
      } catch (error) {
        console.error('Error fetching recent products:', error);
      }
    };

    fetchRecentProducts();
  }, []);

  const renderContent = () => {
    switch (activeMenuItem) {
      case 'Products':
        return <AdminProductManagement />;
      case 'Dashboard':
      default:
        return (
          <>
            <div className="main-header">
              <div className="header-content">
                <div>
                  <h1 className="main-title">Dashboard</h1>
                  <div className="breadcrumb">Dashboard</div>
                </div>
                <button 
                  className="refresh-btn" 
                  onClick={refreshDashboard}
                  disabled={loading}
                >
                  {loading ? 'üîÑ' : 'üîÑ'} Refresh
                </button>
              </div>
            </div>

            {/* KPI Cards */}
            <div className="kpi-section">
              {kpiCards.map((card, index) => (
                <div
                  key={index}
                  className={`kpi-card ${card.title === 'TOTAL PRODUCTS' || card.title === 'TOTAL USERS' ? 'clickable' : ''}`}
                  style={{ backgroundColor: card.bgColor }}
                  onClick={() => handleKPIClick(card.title)}
                >
                  <div className="kpi-content">
                    <h3 className="kpi-title" style={{ color: card.color }}>
                      {card.title}
                    </h3>
                    <div className="kpi-value" style={{ color: card.color }}>
                      {card.value}
                    </div>
                  </div>
                  <div className="kpi-icon" style={{ color: card.color }}>
                    {card.icon}
                  </div>
                  {(card.title === 'TOTAL PRODUCTS' || card.title === 'TOTAL USERS') && (
                    <div className="kpi-hint"></div>
                  )}
                </div>
              ))}
            </div>

            {/* Recent Products Table */}
            <div className="products-section">
              <div className="products-header">
                <h2 className="products-title">Recent Products</h2>
                <button className="view-all-btn">View All</button>
              </div>
              
              <div className="table-container">
                {loading ? (
                  <div className="loading-message">Loading products...</div>
                ) : recentProducts.length > 0 ? (
                  <table className="products-table">
                    <thead>
                      <tr className="table-header">
                        <th>Product ID</th>
                        <th>Product Name</th>
                        <th>Category</th>
                        <th>Price</th>
                        <th>Stock</th>
                        <th>Status</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {recentProducts.map((product, index) => (
                        <tr key={index} className="table-row">
                          <td>{product.id}</td>
                          <td>{product.name}</td>
                          <td>{product.category}</td>
                          <td>{product.price}</td>
                          <td>{product.stock}</td>
                          <td>
                            <span className={`status-badge ${product.status === 'In Stock' ? 'in-stock' : 'low-stock'}`}>
                              {product.status}
                            </span>
                          </td>
                          <td>
                            <div className="action-buttons">
                              <button className="action-btn view-btn" title="View Details">
                                üëÅÔ∏è
                              </button>
                              <button className="action-btn edit-btn" title="Edit Product">
                                ‚úèÔ∏è
                              </button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <div className="no-products-message">No products found. Add some products to get started!</div>
                )}
              </div>
            </div>
          </>
        );
    }
  };

  return (
    <>
      <AdminHeader />
      <div className="admin-dashboard">
        <div className="admin-content">
          {/* Sidebar */}
          <aside className="admin-sidebar">
            <h2 className="sidebar-title">Dashboard</h2>
            <nav className="sidebar-nav">
              {menuItems.map((item) => (
                <button
                  key={item.name}
                  className={`nav-item ${activeMenuItem === item.name ? 'active' : ''}`}
                  onClick={() => setActiveMenuItem(item.name)}
                >
                  <span className="nav-icon">{item.icon}</span>
                  <span className="nav-text">{item.name}</span>
                  <span className="nav-arrow">‚ñ∂</span>
                </button>
              ))}
            </nav>
          </aside>

          {/* Main Content */}
          <main className="admin-main">
            {renderContent()}
          </main>
        </div>

        {/* Bottom Elements */}
        <div className="bottom-elements">
          <div className="progress-bar"></div>
          <button className="subscribe-btn">
            <span className="youtube-icon">‚ñ∂</span>
            Subscribe
          </button>
        </div>
      </div>
    </>
  );
};

export default AdminDashboard;
