import React from 'react';
import ProductCard from './ProductCard';
import './ProductGrid.css';

const ProductGrid = ({ selectedFilters, sortBy }) => {
  // Sample product data - you can replace this with your actual furniture data
  const products = [
    {
      id: 1,
      name: "Modern Sofa Set",
      price: 899.00,
      originalPrice: 1299.00,
      description: "Premium fabric, comfortable seating",
      rating: 4.8,
      reviews: 156,
      image: "/placeholder-sofa.jpg",
      isWishlisted: false,
      isFeatured: false
    },
    {
      id: 2,
      name: "Dining Table Set",
      price: 599.00,
      originalPrice: 799.00,
      description: "Solid wood, 6-seater dining set",
      rating: 4.6,
      reviews: 89,
      image: "/placeholder-dining.jpg",
      isWishlisted: true,
      isFeatured: true
    },
    {
      id: 3,
      name: "Bedroom Wardrobe",
      price: 459.00,
      originalPrice: 659.00,
      description: "Spacious storage, modern design",
      rating: 4.7,
      reviews: 203,
      image: "/placeholder-wardrobe.jpg",
      isWishlisted: false,
      isFeatured: false
    },
    {
      id: 4,
      name: "Coffee Table",
      price: 289.00,
      originalPrice: 389.00,
      description: "Glass top, metal frame",
      rating: 4.5,
      reviews: 67,
      image: "/placeholder-coffee-table.jpg",
      isWishlisted: false,
      isFeatured: false
    },
    {
      id: 5,
      name: "Office Desk",
      price: 349.00,
      originalPrice: 449.00,
      description: "Ergonomic design, cable management",
      rating: 4.9,
      reviews: 124,
      image: "/placeholder-desk.jpg",
      isWishlisted: true,
      isFeatured: false
    },
    {
      id: 6,
      name: "Bookshelf Unit",
      price: 199.00,
      originalPrice: 299.00,
      description: "Adjustable shelves, sturdy construction",
      rating: 4.4,
      reviews: 78,
      image: "/placeholder-bookshelf.jpg",
      isWishlisted: false,
      isFeatured: false
    },
    {
      id: 7,
      name: "TV Stand",
      price: 179.00,
      originalPrice: 249.00,
      description: "Cable management, LED lighting",
      rating: 4.6,
      reviews: 92,
      image: "/placeholder-tv-stand.jpg",
      isWishlisted: false,
      isFeatured: false
    },
    {
      id: 8,
      name: "Accent Chair",
      price: 159.00,
      originalPrice: 229.00,
      description: "Velvet upholstery, gold legs",
      rating: 4.8,
      reviews: 145,
      image: "/placeholder-chair.jpg",
      isWishlisted: true,
      isFeatured: false
    }
  ];

  // Filter products based on selected filters
  const filteredProducts = products.filter(product => {
    // Add your filtering logic here based on selectedFilters
    return true; // For now, show all products
  });

  // Sort products based on sortBy
  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortBy) {
      case 'Price: Low to High':
        return a.price - b.price;
      case 'Price: High to Low':
        return b.price - a.price;
      case 'Rating: High to Low':
        return b.rating - a.rating;
      default:
        return 0;
    }
  });

  return (
    <div className="product-grid-container">
      <h2 className="product-grid-title">Furniture For You!</h2>
      <div className="product-grid">
        {sortedProducts.map(product => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
};

export default ProductGrid;
