import React from 'react';
import './index.css';
import showcaseImg1 from '../assets/placeholder1.jpg';
import showcaseImg2 from '../assets/placeholder2.jpg';
import showcaseImg3 from '../assets/';

const images = [showcaseImg1, showcaseImg2, showcaseImg3];

const MainShowcase = () => (
  <div className="main-showcase-vertical">
    {images.map((img, idx) => (
      <div className="main-showcase-container" key={idx}>
        <button className="arrow left-arrow" aria-label="Previous">&#8592;</button>
        <div className="showcase-image-wrapper">
          <img src={img} alt={`Showcase ${idx + 1}`} className="showcase-image" />
        </div>
        <button className="arrow right-arrow" aria-label="Next">&#8594;</button>
      </div>
    ))}
  </div>
);

export default MainShowcase;
