import React from 'react';
import './MainShowcase.css';
import showcaseImg from '../assets/placeholder1.jpg';

const MainShowcase = () => (
  <div className="main-showcase-container">
    <button className="arrow left-arrow" aria-label="Previous">
      &#8592;
    </button>
    <div className="showcase-image-wrapper">
      <img src={showcaseImg} alt="Showcase" className="showcase-image" />
      <div className="showcase-overlay">
        <div className="showcase-artist">OTIS KWAME</div>
        <div className="showcase-signature">Kwame Kyei</div>
        <div className="showcase-x">X FW</div>
      </div>
      <div className="showcase-caption">
        OTIS KWAME KYEI QUAICOE X FW<br />MAN COLLECTION<br />SUMMER 25
      </div>
    </div>
    <button className="arrow right-arrow" aria-label="Next">
      &#8594;
    </button>
  </div>
);

export default MainShowcase;
