import React, { useEffect, useRef, useState } from 'react';
import './Header.css';

const Header = () => {
  const [whiteText, setWhiteText] = useState(false);
  const headerRef = useRef(null);

  useEffect(() => {
    const header = headerRef.current;
    const darkSection = document.getElementById('dark-section');
    if (!header || !darkSection) return;

    const observer = new window.IntersectionObserver(
      ([entry]) => {
        setWhiteText(entry.isIntersecting);
      },
      {
        root: null,
        threshold: 0,
        rootMargin: `-${header.offsetHeight}px 0px 0px 0px`,
      }
    );
    observer.observe(darkSection);
    return () => observer.disconnect();
  }, []);

  return (
    <header ref={headerRef} className={`header${whiteText ? ' header--white-text' : ''}`}>
      <div className="header__menu-icon">
        <span className="menu-icon-bar"></span>
        <span className="menu-icon-bar"></span>
        <span className="menu-icon-bar"></span>
      </div>
      <div className="header__logo">FW</div>
      <div className="header__search">
        <input type="text" placeholder="SEARCH" />
      </div>
      <nav className="header__nav">
        <a href="#login">LOGIN</a>
        <a href="#help">HELP</a>
        <a href="#cart">FURNI BAG [0]</a>
      </nav>
    </header>
  );
};

export default Header;
