import React, { useState } from 'react';
import './MainPage.css';
import showcaseImg1 from '../assets/placeholder1.jpg';
import showcaseImg2 from '../assets/placeholder2.jpg';
import showcaseImg3 from '../assets/console table.jpg';

const images = [showcaseImg1, showcaseImg2, showcaseImg3];

const MainPage = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [hovered, setHovered] = useState(false);

  const prevImage = () => {
    setCurrentIndex((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  const nextImage = () => {
    setCurrentIndex((prev) => (prev === images.length - 1 ? 0 : prev + 1));
  };

  return (
    <div className="main-showcase-vertical">
      <div className="main-showcase-container">
        <button className="arrow left-arrow" aria-label="Previous" onClick={prevImage}>
          &#x276E;
        </button>
        <div
          className="showcase-image-wrapper"
          onMouseEnter={() => setHovered(true)}
          onMouseLeave={() => setHovered(false)}
        >
          <img
            src={images[currentIndex]}
            alt={`Showcase ${currentIndex + 1}`}
            className={`showcase-image ${hovered ? 'zoom-in' : ''}`}
          />
        </div>
        <button className="arrow right-arrow" aria-label="Next" onClick={nextImage}>
          &#x276F;
        </button>
      </div>
    </div>
  );
};

export default MainPage;
