import React from 'react';
import Header from './Header';
import './UserProfilePage.css';
import { useNavigate } from 'react-router-dom';

const menuItems = [
  'PURCHASES',
  'EXCHANGES AND RETURNS',
  'NOTIFICATIONS',
  'PAYMENT METHODS',
  'PROFILE',
  'SETTINGS',
  '',
  'FAVOURITES',
  'HELP',
  'DOWNLOAD APP',
];

const UserProfilePage = () => {
  const navigate = useNavigate();
  // Get user info from localStorage
  const user = JSON.parse(localStorage.getItem('user')) || {};

  const handleSignOut = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
    window.location.reload();
  };

  const handleDeleteAccount = async () => {
    if (!window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) return;
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/auth/profile', {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      const data = await response.json();
      if (data.success) {
        alert('Account deleted successfully.');
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/');
        window.location.reload();
      } else {
        alert(data.message || 'Failed to delete account.');
      }
    } catch (error) {
      alert('Network error. Please try again.');
    }
  };

  return (
    <>
      <Header />
      <div className="profile-page-container">
        <aside className="profile-sidebar">
          <ul>
            {menuItems.map((item, idx) =>
              item ? (
                <li key={item} className={item === 'PROFILE' ? 'active' : ''}>{item}</li>
              ) : (
                <li key={idx} className="profile-sidebar-spacer"></li>
              )
            )}
          </ul>
        </aside>
        <main className="profile-main">
          <div className="profile-main-content">
            <div className="profile-user-name">{user.name?.toUpperCase()} {user.surname?.toUpperCase()}</div>
            <div className="profile-info-list">
              <div className="profile-info-label">ADDRESSES</div>
              <div className="profile-info-label">MY MEASUREMENTS</div>
              <div className="profile-info-label">EMAIL</div>
              <div className="profile-info-value">{user.email}</div>
              <div className="profile-info-label">PHONE</div>
              <div className="profile-info-value">{user.phone_prefix} {user.telephone}</div>
              <div className="profile-info-label">PASSWORD</div>
              <div className="profile-info-value">{'..........'}</div>
            </div>
            <button className="profile-signout-btn" onClick={handleSignOut}>SIGN OUT</button>
            <button className="profile-delete-btn" onClick={handleDeleteAccount}>DELETE ACCOUNT</button>
          </div>
        </main>
      </div>
    </>
  );
};

export default UserProfilePage;
