import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AdminHeader from './AdminHeader';
import AdminProductManagement from './AdminProductManagement';
import { productAPI } from '../services/api';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [activeMenuItem, setActiveMenuItem] = useState('Dashboard');
  const [dashboardData, setDashboardData] = useState({
    totalProducts: 0,
    totalCategories: 0,
    totalUsers: 0,
    websiteVisits: 0
  });
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Fetch dashboard data
  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        
        // Fetch products to get total count
        const productsResponse = await productAPI.getAllProducts();
        const totalProducts = productsResponse.success ? (productsResponse.data?.length || 0) : 0;
        
        // Get unique categories from products
        const categories = productsResponse.success && productsResponse.data 
          ? [...new Set(productsResponse.data.map(product => product.category).filter(Boolean))]
          : [];
        const totalCategories = categories.length;
        
        // For now, we'll use placeholder data for users and visits
        // You can implement these API calls later
        const totalUsers = 156; // Placeholder - implement user API later
        const websiteVisits = 1234; // Placeholder - implement analytics API later
        
        setDashboardData({
          totalProducts,
          totalCategories,
          totalUsers,
          websiteVisits
        });
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const menuItems = [
    { name: 'Dashboard', icon: '📊' },
    { name: 'Products', icon: '📦' },
    { name: 'Categories', icon: '📂' },
    { name: 'Locations', icon: '📍' },
    { name: 'Customers', icon: '👥' }
  ];

  const kpiCards = [
    {
      title: 'TOTAL PRODUCTS',
      value: loading ? '...' : dashboardData.totalProducts.toString(),
      icon: '📦',
      color: '#3B82F6',
      bgColor: '#EFF6FF'
    },
    {
      title: 'TOTAL CATEGORIES',
      value: loading ? '...' : dashboardData.totalCategories.toString(),
      icon: '📂',
      color: '#8B5CF6',
      bgColor: '#F3F4F6'
    },
    {
      title: 'TOTAL USERS',
      value: loading ? '...' : dashboardData.totalUsers.toString(),
      icon: '👥',
      color: '#10B981',
      bgColor: '#ECFDF5'
    },
    {
      title: 'WEBSITE VISITS',
      value: loading ? '...' : dashboardData.websiteVisits.toLocaleString(),
      icon: '📊',
      color: '#F59E0B',
      bgColor: '#FFFBEB'
    }
  ];

  const recentProducts = [
    {
      id: '1',
      name: 'Modern Sofa Set',
      category: 'Living Room',
      price: '$1,299',
      stock: '15',
      status: 'In Stock'
    },
    {
      id: '2',
      name: 'Dining Table',
      category: 'Dining Room',
      price: '$899',
      stock: '8',
      status: 'In Stock'
    },
    {
      id: '3',
      name: 'Bed Frame',
      category: 'Bedroom',
      price: '$599',
      stock: '12',
      status: 'In Stock'
    },
    {
      id: '4',
      name: 'Office Chair',
      category: 'Office',
      price: '$299',
      stock: '5',
      status: 'Low Stock'
    }
  ];

  const renderContent = () => {
    switch (activeMenuItem) {
      case 'Products':
        return <AdminProductManagement />;
      case 'Dashboard':
      default:
        return (
          <>
            <div className="main-header">
              <h1 className="main-title">Dashboard</h1>
              <div className="breadcrumb">Dashboard</div>
            </div>

            {/* KPI Cards */}
            <div className="kpi-section">
              {kpiCards.map((card, index) => (
                <div
                  key={index}
                  className="kpi-card"
                  style={{ backgroundColor: card.bgColor }}
                >
                  <div className="kpi-content">
                    <h3 className="kpi-title" style={{ color: card.color }}>
                      {card.title}
                    </h3>
                    <div className="kpi-value" style={{ color: card.color }}>
                      {card.value}
                    </div>
                  </div>
                  <div className="kpi-icon" style={{ color: card.color }}>
                    {card.icon}
                  </div>
                </div>
              ))}
            </div>

            {/* Recent Products Table */}
            <div className="products-section">
              <div className="products-header">
                <h2 className="products-title">Recent Products</h2>
                <button className="view-all-btn">View All</button>
              </div>
              
              <div className="table-container">
                <table className="products-table">
                  <thead>
                    <tr className="table-header">
                      <th>Product ID</th>
                      <th>Product Name</th>
                      <th>Category</th>
                      <th>Price</th>
                      <th>Stock</th>
                      <th>Status</th>
                      <th>Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {recentProducts.map((product, index) => (
                      <tr key={index} className="table-row">
                        <td>{product.id}</td>
                        <td>{product.name}</td>
                        <td>{product.category}</td>
                        <td>{product.price}</td>
                        <td>{product.stock}</td>
                        <td>
                          <span className={`status-badge ${product.status === 'In Stock' ? 'in-stock' : 'low-stock'}`}>
                            {product.status}
                          </span>
                        </td>
                        <td>
                          <div className="action-buttons">
                            <button className="action-btn view-btn" title="View Details">
                              👁️
                            </button>
                            <button className="action-btn edit-btn" title="Edit Product">
                              ✏️
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </>
        );
    }
  };

  return (
    <>
      <AdminHeader />
      <div className="admin-dashboard">
        <div className="admin-content">
          {/* Sidebar */}
          <aside className="admin-sidebar">
            <h2 className="sidebar-title">Dashboard</h2>
            <nav className="sidebar-nav">
              {menuItems.map((item) => (
                <button
                  key={item.name}
                  className={`nav-item ${activeMenuItem === item.name ? 'active' : ''}`}
                  onClick={() => setActiveMenuItem(item.name)}
                >
                  <span className="nav-icon">{item.icon}</span>
                  <span className="nav-text">{item.name}</span>
                  <span className="nav-arrow">▶</span>
                </button>
              ))}
            </nav>
          </aside>

          {/* Main Content */}
          <main className="admin-main">
            {renderContent()}
          </main>
        </div>

        {/* Bottom Elements */}
        <div className="bottom-elements">
          <div className="progress-bar"></div>
          <button className="subscribe-btn">
            <span className="youtube-icon">▶</span>
            Subscribe
          </button>
        </div>
      </div>
    </>
  );
};

export default AdminDashboard;
