import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from './Header';
import './ProductDetailPage.css';

const ProductDetailPage = () => {
  const { productId } = useParams();
  const [selectedColor, setSelectedColor] = useState('Red');
  const [quantity, setQuantity] = useState(1);

  // Mock product data (in real app, this would come from API)
  const product = {
    id: productId,
    name: 'Modern Comfort Sofa',
    description: 'A perfect balance of comfort and style for your living room.',
    price: 1299.00,
    monthlyPrice: 99.99,
    rating: 4.5,
    reviews: 121,
    colors: ['Red', 'Black', 'Gray', 'Blue', 'White'],
    selectedColor: selectedColor,
    stock: 12,
    category: 'Sofa',
    images: [
      '/sofa-red-main.jpg',
      '/sofa-red-1.jpg',
      '/sofa-red-2.jpg',
      '/sofa-red-3.jpg'
    ],
    breadcrumb: 'Furniture / Living Room / Sofas / Modern Comfort Sofa'
  };

  const handleQuantityChange = (change) => {
    const newQuantity = quantity + change;
    if (newQuantity >= 1 && newQuantity <= product.stock) {
      setQuantity(newQuantity);
    }
  };

  const handleColorSelect = (color) => {
    setSelectedColor(color);
  };

  const renderStars = (rating) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={`star ${i <= rating ? 'filled' : ''}`}>
          ★
        </span>
      );
    }
    return stars;
  };

  return (
    <>
      <Header />
      <div className="product-detail-page">
        <div className="product-detail-container">
          {/* Breadcrumb */}
          <div className="breadcrumb-nav">
            <span className="breadcrumb-text">{product.breadcrumb}</span>
          </div>

          <div className="product-detail-content">
            {/* Left Panel - Product Images */}
            <div className="product-images-section">
              <div className="main-image-container">
                <img 
                  src={product.images[0]} 
                  alt={product.name} 
                  className="main-product-image"
                />
              </div>
              <div className="thumbnail-images">
                {product.images.map((image, index) => (
                  <div key={index} className="thumbnail-container">
                    <img 
                      src={image} 
                      alt={`${product.name} ${index + 1}`} 
                      className="thumbnail-image"
                    />
                  </div>
                ))}
              </div>
            </div>

            {/* Right Panel - Product Details */}
            <div className="product-details-section">
              <h1 className="product-name">{product.name}</h1>
              <p className="product-description">{product.description}</p>
              
              {/* Rating */}
              <div className="product-rating">
                <div className="stars">
                  {renderStars(product.rating)}
                </div>
                <span className="reviews-count">({product.reviews})</span>
              </div>

              {/* Pricing */}
              <div className="product-pricing">
                <div className="price-main">
                  ${product.price.toFixed(2)} or ${product.monthlyPrice}/month
                </div>
                <div className="price-note">
                  Suggested payments with 6 months special financing
                </div>
              </div>

              {/* Color Selection */}
              <div className="color-selection">
                <h3>Choose a Color</h3>
                <div className="color-options">
                  {product.colors.map((color) => (
                    <button
                      key={color}
                      className={`color-option ${selectedColor === color ? 'selected' : ''}`}
                      onClick={() => handleColorSelect(color)}
                      style={{ backgroundColor: color.toLowerCase() }}
                    >
                      {color}
                    </button>
                  ))}
                </div>
              </div>

              {/* Quantity and Stock */}
              <div className="quantity-section">
                <div className="quantity-selector">
                  <button 
                    className="quantity-btn"
                    onClick={() => handleQuantityChange(-1)}
                    disabled={quantity <= 1}
                  >
                    -
                  </button>
                  <span className="quantity-display">{quantity}</span>
                  <button 
                    className="quantity-btn"
                    onClick={() => handleQuantityChange(1)}
                    disabled={quantity >= product.stock}
                  >
                    +
                  </button>
                </div>
                <div className="stock-info">
                  Only {product.stock} Items Left! Don't miss it
                </div>
              </div>

              {/* Action Buttons */}
              <div className="action-buttons">
                <button className="buy-now-btn">Buy Now</button>
                <button className="add-to-cart-btn">Add to Cart</button>
              </div>

              {/* Delivery Information */}
              <div className="delivery-info">
                <div className="delivery-item">
                  <div className="delivery-icon">🚚</div>
                  <div className="delivery-content">
                    <h4>Free Delivery</h4>
                    <p>Enter your Postal code for Delivery Availability</p>
                  </div>
                </div>
                <div className="delivery-item">
                  <div className="delivery-icon">📦</div>
                  <div className="delivery-content">
                    <h4>Return Delivery</h4>
                    <p>Free 30 days Delivery Returns. Details</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProductDetailPage;
